// Estructura básica de un programa Java
public class MiPrimerPrograma {
    public static void main(String[] args) {
        System.out.println("¡Hola Mundo!");
    }
}

// Programa interactivo con entrada del usuario
import java.util.Scanner;

public class Saludo {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        System.out.print("Ingresa tu nombre: ");
        String nombre = scanner.nextLine();
        System.out.println("Hola, " + nombre + "!");
        scanner.close();
    }
}

// Declaración y asignación de variables
int edad = 25;
double altura = 1.75;
String nombre = "Juan";
boolean esEstudiante = true;
char inicial = 'J';

// Uso de variables en operaciones
int a = 10;
int b = 5;
int suma = a + b;
System.out.println("La suma es: " + suma);

// Programa básico con método main
public class MiPrograma {
    public static void main(String[] args) {
        System.out.println("¡Mi primer programa!");
        System.out.println("Java es genial");
    }
}

// Programa con variables y salida
public class Calculadora {
    public static void main(String[] args) {
        int numero1 = 15;
        int numero2 = 25;
        int resultado = numero1 + numero2;
        System.out.println("El resultado es: " + resultado);
    }
}

// Estructura if básica
int edad = 18;
if (edad >= 18) {
    System.out.println("Eres mayor de edad");
}

// Estructura if-else-if
int puntuacion = 85;
if (puntuacion >= 90) {
    System.out.println("Excelente");
} else if (puntuacion >= 70) {
    System.out.println("Bueno");
} else {
    System.out.println("Necesitas mejorar");
}

// Bucle for
for (int i = 1; i <= 5; i++) {
    System.out.println("Iteración: " + i);
}

// Bucle while
int contador = 0;
while (contador < 3) {
    System.out.println("Contador: " + contador);
    contador++;
}

// Método simple sin parámetros
public static void saludar() {
    System.out.println("¡Hola desde el método!");
}

// Llamada al método
saludar();

// Método con parámetros y valor de retorno
public static int sumar(int a, int b) {
    return a + b;
}

// Uso del método
int resultado = sumar(5, 3);
System.out.println("Resultado: " + resultado);
